
public class Shape {

		// TODO Auto-generated method stub
		public void area(int x,int y)
		{
			System.out.println("Area= "+x*y+"\n");
		
		}
		public void area(double x,double y)
		{
			System.out.println("Area= "+0.5*x*y+"\n");
		
		}
		public void area(int x)
		{
			System.out.println("Area= "+x*x+"\n");
		
		}
		public void area(double x)
		{
			System.out.println("Area= "+4*3.1416*x*x+"\n");
		
		}
		public void area(int a,int b,int c)
		{
			System.out.println("Area= "+a*b*c+"\n");
		}
	

}
